styles.css:
:where(.wp-site-blocks *:focus){outline-width:2px;outline-style:solid}
/* 以下は一時的に自分で設定している */

/* Projects一覧セクション */
.sec-projects-archive {
  padding-block: 80px;
}

/* 1プロジェクトぶんの帯 */
.project-band {
  position: relative;
  padding: 32px 40px;
  margin-bottom: 32px;
  border-radius: 9999px;         /* 超大きい半径 = 丸みの帯 */
  border: 1px solid #111;        /* 円の縁っぽいライン */
  background-color: #fff;
  overflow: hidden;              /* 中身がはみ出さないように */
}

/* 中身の2カラムを横並びで綺麗に */
.project-band .wp-block-columns {
  margin: 0;
}

.project-band .wp-block-column:first-child {
  flex-basis: 30%;
}

.project-band .wp-block-column:last-child {
  flex-basis: 70%;
}

/* アイキャッチ画像 */
.project-band img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 8px;  /* ここは四角でも丸でも好みで */
  object-fit: cover;
}

/* テキスト側 */
.project-band__body {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

/* タイトル */
.project-band__body .wp-block-post-title {
  font-size: 1.4rem;
  margin: 0;
}

/* 説明文（抜粋など） */
.project-band__body .wp-block-post-excerpt,
.project-band__body p {
  font-size: 0.9rem;
  line-height: 1.7;
  margin: 0;
}

/* SNSアイコン（Instagram） */
.project-band__body .wp-block-social-links {
  margin-top: 12px;
  justify-content: flex-start;
}

/*これ以下は個別Projectsページ*/
/* Project Single Hero */
.sec-project-single-hero {
  padding-block: 80px;
}

.sec-project-single-hero .wp-block-columns {
  align-items: center;
  gap: 40px;
}

.project-single-meta {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

/* 日本語タイトル */
.project-single-meta .wp-block-post-title {
  font-size: 2rem;
  margin: 0;
}

/* 英タイトル（ACF） */
.project-single-meta .wp-block-shortcode:first-of-type {
  font-size: 1rem;
  letter-spacing: 0.16em;
  text-transform: uppercase;
  opacity: 0.7;
}

/* 抜粋（リード） */
.project-single-meta .wp-block-post-excerpt {
  margin-top: 8px;
  font-size: 0.95rem;
  line-height: 1.8;
}

/* 外部リンクボタン */
.btn-external {
  display: inline-block;
  margin-top: 16px;
  padding: 10px 28px;
  border-radius: 9999px;
  border: 1px solid #111;
  background-color: #111;
  color: #fff;
  font-size: 0.9rem;
  text-decoration: none;
  letter-spacing: 0.08em;
}

.btn-external:hover {
  background-color: #000;
}

/* アイキャッチ画像 */
.sec-project-single-hero .wp-block-post-featured-image img {
  width: 100%;
  height: auto;
  display: block;
  object-fit: cover;
}

/* 本文 */
.sec-project-single-body {
  padding-block: 40px 80px;
  max-width: 800px;
  margin-inline: auto;
}
